// ];

// let sample_list = new Array();

// console.log("sample is : ", sample_list);

function App() {
  const [list, setList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setList(response.data);
    });
  });

  console.log("list is :", list);

  const reciveData = (data) => {
    let New_expence = {
      id: Math.floor(Math.random() * 100),
      ...data,
    };
    submitReview(New_expence);
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setList(response.data);
    });
    // const update_list = [New_expence, ...list];
  };

  const del = () => {
    console.log("press");
  };

  return (
    <div className="list">
      <center>
        <h1 className="head">EXPENSE TRACKER</h1>
      </center>
      <NewExpences addExpence={reciveData} />
      <br></br>
      <Card className="card">
        <Expenses item={list} remove={del} />
      </Card>
    </div>
  );
}
export default App;
