import "./Date.css";
function Date(props) {
  const day = props.date.toLocaleString("en-us", { day: "2-digit" });
  const month = props.date.toLocaleString("en-us", { month: "short" });
  const year = props.date.getFullYear();
  return (
    <div>
      <p className="date">
        DATE : {day} {month} {year}
      </p>
    </div>
  );
}
export default Date;
