import { BrowserRouter, Route, Routes } from "react-router-dom";
import Home from "./components/Home";
import Cart from "./components/Cart";
import Login from "./components/Login";
import Search from "./components/Search";
import Signup from "./components/Signup";
import Customer_Home from "./components/Customer_Home";
import Vendor_Home from "./components/Vendor_Home";
import { useState } from "react";
function App() {
  const [v_data, setVData] = useState({});
  const [c_data, setCData] = useState({});
  const customer_info = (info) => {
    setCData(info);
  };
  const vendor_info = (info) => {
    setVData(info);
  };
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/Customer_Home"
            element={
              <Customer_Home get_data={customer_info} customer_data={c_data} />
            }
          ></Route>
          <Route
            path="/Vendor_Home"
            element={
              <Vendor_Home get_data={vendor_info} vendor_data={v_data} />
            }
          ></Route>
          <Route path="/Search" element={<Search />}></Route>
          <Route path="/Login" element={<Login />}></Route>
          <Route path="/Signup" element={<Signup />}></Route>
          <Route path="/Cart" element={<Cart />}></Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
