import "./Item.css";
import Date_comp from "./Date";

function Item(props) {
  const rem = () => {
    props.clear(props.id);
  };

  return (
    <div>
      <div className="item">
        <Date_comp date={props.date}></Date_comp> {/* multi-Component */}
        <p className="name">{props.name}</p>
        <p className="price">${props.price}</p>
        <input type="button" value="x" className="cancle" onClick={rem} />
      </div>
      <br />
    </div>
  );
}
export default Item;
