import React, { useState, useEffect } from "react";
import Expenses from "./components/Expenses";
import "./App.css";
import Card from "./components/Card";
import NewExpences from "./components/NewExpences";
const mysql = require("mysql");
// import Axios from "axios";
// const mysql = require("mysql");
// const submitReview = (New_expence) => {
// console.log(New_expence);
// Axios.post("http://localhost:3001/api/send", { ...New_expence });
// };
const db = mysql.createPool({
  host: "localhost",
  user: "root",
  password: "",
  database: "expences",
});
function App() {
  const [list, setList] = useState([]);

  // useEffect(() => {
  //   Axios.get("http://localhost:3001/api/get").then((response) => {
  //     setList(response.data);
  //   });
  // }, []);
  const get_data = () => {
    const select_query = "select * from expencedata order by item_date DESC;";

    db.query(select_query, (err, result) => {
      console.log(err);
      console.log(result);
    });
  };
  get_data();

  const reciveData = (data) => {
    // let New_expence = {
    //   id: Math.floor(Math.random() * 100),
    //   ...data,
    // };
    // submitReview(New_expence);
    const name = data.item_name;
    const price = data.item_price;
    const date = data.item_date;

    const insert_query =
      "insert into expencedata(item_name,item_date,item_price) values (?,?,?);";
    db.query(insert_query, [name, date, price], (err, result) => {
      console.log(err);
    });
    get_data();
    // Axios.get("http://localhost:3001/api/get").then((response) => {
    //   setList(response.data);
    // });
    // Axios.get("http://localhost:3001/api/get").then((response) => {
    //   setList(response.data);
    // });
  };

  const deleteHandeler = (id) => {
    // Axios.post("http://localhost:3001/api/delete", { item_id: id });
    // Axios.get("http://localhost:3001/api/get").then((response) => {
    //   setList(response.data);
    // });
    // Axios.get("http://localhost:3001/api/get").then((response) => {
    //   setList(response.data);
    // });
    const delete_query = "DELETE from expencedata WHERE id= ? ";
    db.query(delete_query, [id], (err, result) => {
      console.log(err);
      console.log(result);
      get_data();
    });
  };

  return (
    <div className="list">
      <center>
        <h1 className="head">EXPENSE TRACKER</h1>
      </center>
      <NewExpences addExpence={reciveData} />
      <br></br>
      <Card className="card">
        <Expenses item={list} remove={deleteHandeler} />
      </Card>
    </div>
  );
}
export default App;
