import React, { useState } from "react";
import "./NewExpences.css";

const NewExpences = () => {
  let date = new Date();
  let Name = "";
  let price = 0;

  const [newDate, setNewDate] = useState(date);
  const [newName, setnewName] = useState(Name);
  const [newPrice, setNewPrice] = useState(price);

  const changeDateHandeler = (event) => {
    setNewDate(event.target.value);
  };
  const changeNameHandeler = (event) => {
    setnewName(event.target.value);
  };
  const changePriceHandeler = (event) => {
    setNewPrice(event.target.value);
  };

  const setValue = (event) => {
    event.preventDefault();
    setNewDate(date);
    setnewName(Name);
    setNewPrice(price);
  };

  return (
    <div className="newExpence">
      <form onSubmit={setValue}>
        <div className="input">
          <lable className="setdate">Enter Date:</lable>
          <input type="date" value={newDate} onChange={changeDateHandeler} />
        </div>

        <div className="input">
          <lable className="setname">Enter Name:</lable>
          <input type="text" value={newName} onChange={changeNameHandeler} />
        </div>

        <div className="input">
          <lable className="setprice">Enter price:</lable>
          <input
            type="number"
            min="0.1"
            step="0.1"
            value={newPrice}
            onChange={changePriceHandeler}
          />
        </div>
        <div className="button">
          <button>Add</button>
        </div>
      </form>
    </div>
  );
};

export default NewExpences;
